import React, { Component, useState, useEffect } from 'react';
import './SearchBar.css';
import { Button } from "../Button";
import EventCard from '../EventCard/EventCard';

function SearchBar({placeholder, input}) {
    const [search, setSearch] = useState("");
    const [users, setUsers] = useState([]);
    const [query, setQuery] = useState('dance');

    useEffect(() => {
        fetch(`/events/${query}`)
          .then((res) => res.json())
          .then((data) => {
            setUsers(data);
          })
          .catch((err) => {
            console.log(err);
          });
    }, []);

    const updateSearch = e => {
        setSearch(e.target.value);
        console.log(search);
    }

    const getSearch = e => {
        e.preventDefault();
        setQuery(search);
    }

    return(
        <div className="search">
            <form className="searchInputs">
                <input type="text" placeholder={placeholder} value={search} onChange={updateSearch}/>
                <div className="searchIcon"></div>
            </form>

            <div className="dataResult">
                {users.map(data => (
                    <EventCard
                        key={data.EID}
                        title={data.eventName}
                        startDate={data.startDateTime}
                        image={"https://images.unsplash.com/photo-1472457897821-"
                        + "70d3819a0e24?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8c21hbGx8ZW58MHx8MHx8&w=1000&q=80"}
                    />
                ))}
            </div>
        </div>
    )
}
export default SearchBar;
